/// <summary>
/// Invokes the method $propertyName$.
/// </summary>
/// <param name="inArgs">The input arguments.</param>
/// <returns>A <see cref="Task"/> that returns the output arguments.</returns>
public async Task<object[]> $propertyName$(params object[] inArgs)
{
    var response = await UaTcpSessionClient.FromModel(this).CallAsync(new CallRequest
    {
       MethodsToCall = new[]
        {
            new CallMethodRequest
            {
                ObjectId = NodeId.Parse("$parentNodeId$"),
                MethodId = NodeId.Parse("$nodeId$"),
                InputArguments = inArgs.ToVariantArray()
            }
        }
    });

    var result = response.Results[0];
    if (StatusCode.IsBad(result.StatusCode))
    {
        throw new ServiceResultException(new ServiceResult(result.StatusCode));
    }

    return result.OutputArguments.ToObjectArray();
}

